actionConfiguration.body:
() => {
  const dataSource = {
    chart: {
      showpercentvalues: "1",
      defaultcenterlabel: "Hover",
      decimals: "1",
      centerlabel: "$label: $value",
      theme: "fusion",
      showLegend: "1",
      showLabels: "0",
      showValues: "0"
    },
    data: [{
      label: "Card",
      value: refundMethod.data[0].value,
      color: "#7441F6"
    }, {
      label: "Credit",
      value: refundMethod.data[1].value,
      color: "#00BC62"
    }]
  };
  return {
    type: "doughnut2d",
    dataFormat: "json",
    dataSource
  };
}

